INSERT INTO `love_letter` VALUES ('1200000000000060032', '吾柬友', '陈丹妮', '想找个可爱的人 然后我可以送你限量版纸飞机 上面的蜜语只有我跟你知道 或者睡不着的午后我们到无边湖旁坐着发呆 你不开心带你吃牛肉 给你满满一口好心情  大概就是出生时被随手附赠了一罐温柔 遇见你的时候 恰好还可以挥霍着使  我们相爱吧 不非得白宫前 榕树下 在心底的', '120000000000006', '2b74c61d2ce0e87923220234f3619b7d');
