INSERT INTO `love_letter` VALUES ('1200000000000070009', '匿名', '小7小7', '虽然听了你为什么叫小7后有点小吃醋，但发现你好像很喜欢这个数字，我也超想你会喜欢想让你上第七期的我。从军训到现在不知过了多少个7天，但我对你的喜欢还是那一个喜欢。对啊，刘兆熹!小7!我就是喜欢你啊。', '120000000000007', '2b74c61d2ce0e87923220234f3619b7d');
