INSERT INTO `love_letter` VALUES ('1200000000000070012', '厚黑', 'F翠花', '我喜欢你，从第一眼看到你就喜欢你。真的，我一直以为我在大学期间不会想要谈恋爱，直到遇见了你，是你不经意间的动作，触动我内心的那条情弦，从此一发不可收拾。我会一遍一遍的浏览你的朋友圈，一遍又一遍，只想更好的了解你。我喜欢你，可能你也感觉到了。', '120000000000007', '2b74c61d2ce0e87923220234f3619b7d');
