INSERT INTO `love_letter` VALUES ('1200000000000080019', '宋仲J', '傻猪猪', 'bb虽然我们现在在一起的时间不长，但以后的日子还长着呢，我会在你身旁陪着你，会和你去更多的地方，和你一起面对未来。虽然我经常惹你生气，每天都被你翻白眼，翻得隐形眼镜都掉出来了，有时候还会说错话，不会怎么哄你。但是，我还是爱你的', '120000000000008', '2b74c61d2ce0e87923220234f3619b7d');
