INSERT INTO `love_letter` VALUES ('1200000000000080042', 'GWT', '13旅管的杨依晴', '虽然你是大三的师姐，可是自从一起和你上课我就留意到了你，喜欢看你笑起来有淡淡的酒窝，希望以后每一天都能看到你', '120000000000008', '2b74c61d2ce0e87923220234f3619b7d');
