INSERT INTO `love_letter` VALUES ('1200000000000090036', '爸爸', '大狗子', '别人觉得你丑，你自己也嫌弃自己，又黑又矮，头发还乱糟糟的，不过你很好，人品好，估计是因为你丑的缘故，所以你就养成了你人品特别好的缘故吧，情人眼里出西施，你最美，友谊的小船永远不翻，我懂你', '120000000000009', '2b74c61d2ce0e87923220234f3619b7d');
