INSERT INTO `love_letter` VALUES ('1200000000000090038', '爱你的人儿', '萝莉', '我爱过的女生不多，至少你是我爱得最深的一个，我对你的感情已陷得太深，我真的害怕被你拒绝后，一夜间变成了彼此的陌生人，不再联系，我最害怕的就是我最爱的人突然离去，或在漫长的等待中，被漫长的岁月冲淡了这份感情，变得陌生，你我变成了过路人，我爱你，简简单单的三个字，仅此而已', '120000000000009', '2b74c61d2ce0e87923220234f3619b7d');
