INSERT INTO `love_letter` VALUES ('1200000000000100044', '你亲爱的佳佳', '祝成术', '今年你的生日我依然可以和你一起度过，我真的好开心，和你相识已经快三年了。其实我喜欢你很久了，不知道你是否察觉，你的每次装逼我都觉得你真的好帅，你就是我心中的华软刘德华，不管其他人怎么看。我爱你成术！', '120000000000010', '2b74c61d2ce0e87923220234f3619b7d');
