INSERT INTO `love_letter` VALUES ('1200000000000110002', 'bitch toe', '吖泰尔', '我们一年了，中间经历了很多事情，为了你我哭过很多次，我好多次在伤心的抉择，我终究没有放手。尽管在学校我和你宛如陌生人，但是我爱你，我等你', '120000000000011', '2b74c61d2ce0e87923220234f3619b7d');
