INSERT INTO `love_letter` VALUES ('1200000000000110035', '余文乐', '庞丹颖', '你知道吗，我从第一眼看见你的时候，感觉身边的花都是为你开放的，而你在花丛中就是一道最美丽的风景，虽然你这辈子都不会可能知道我是谁，但你只要知道会有一个人默默喜欢你就好', '120000000000011', '2b74c61d2ce0e87923220234f3619b7d');
