INSERT INTO `love_letter` VALUES ('1200000000000120037', '雨晴', '那家伙', '你这不听话的孩子，什么时候才可以听话啊？一旦认真，便会看得比自己还重要！只想让你知道，每一次都会有胸口痛！每次我都以为会是最后一次，结果并非如此！日后怎办，我不知道，开始有了逃避的想法，因为在那几刻，会感到累和无助！只因为3+2，所以才会心痛', '120000000000012', '2b74c61d2ce0e87923220234f3619b7d');
