INSERT INTO `love_letter` VALUES ('1200000000000130049', '吴麟波', '廖静雯', '我这辈子最大的幸运就是认识你, 而最大的不幸却是不能拥有你.也许你会遇到你深爱的人,可是却不会遇到第二人像我这么爱你的人.  ', '120000000000013', '2b74c61d2ce0e87923220234f3619b7d');
INSERT INTO `love_letter` VALUES ('1200000000000130050', '匿名', '大 boss', '大 boss先生，你好么？我想你了，永远都像第一次见你那样一道光照进我生命里。可是你却总看不见我，强行追你都没用，希望你很好', '120000000000013', '2b74c61d2ce0e87923220234f3619b7d');
