INSERT INTO `love_letter` VALUES ('1200000000000140020', '高粱中二', '安琪', '喜欢你这么久，曾经几乎算是走在一起，可惜怪我不懂事。造化弄人，从此已是陌生人，多想再回到以前的日子，再给我一次机会去勇敢一次。很希望能够有一次机会再来一次邂逅。', '120000000000014', '2b74c61d2ce0e87923220234f3619b7d');
