INSERT INTO `love_letter` VALUES ('1200000000000160021', '弄仔ฅ(♡ơ ₃ơ)ฅ', '弄仔(o^∀^o)', '初恋这回事～真的是说奇妙就真的很奇妙～2010.1--2016.6，我们在一起五年半了～|･ω･｀)在下一年的纪念日～我们还会不忘初心说一句“我喜欢你～”ฅ( ̳• ε • ̳)ฅ', '120000000000016', '2b74c61d2ce0e87923220234f3619b7d');
