INSERT INTO `love_letter` VALUES ('1200000000000170049', '匿名', 'you', '最近每天都可以因为你一句话哭的要死，你就不可以哄一下我呀，然而我觉得我们感情慢慢在加深，你开始在乎我的感受，我也不去听那些流言蜚语，我信任你的。爱你，不要怕以后，未来还很长。', '120000000000017', '2b74c61d2ce0e87923220234f3619b7d');
