INSERT INTO `love_letter` VALUES ('1200000000000200005', '匿名', 'chen杰he', '有人说平行线最可怕；但我觉得最可怕的是相交线，明明他们有过相交，却总会在以后某个时刻相互远离，而且越行越远……我情愿八年前我们从不相遇，而不是留下永远的遗憾，祝你幸福', '120000000000020', '2b74c61d2ce0e87923220234f3619b7d');
