INSERT INTO `love_letter` VALUES ('1200000000000200019', '任性的竹竿', '大师兄', '我萌萌哒的大师兄呀～虽然我们在一起没多久但是总感觉已经是一辈子了。。她说过我在你面前就像是生活不能自理的小女孩。。而我也享受着你对我的疼爱。。真的我想说享受一个人的\n宠爱是会上瘾的。。你已经让我上瘾了。所以你必须对我负责啊。以后的路我也不能再一个人走下去了。', '120000000000020', '2b74c61d2ce0e87923220234f3619b7d');
