INSERT INTO `love_letter` VALUES ('1200000000000270009', 'L', '秀珠', '自從第一次見到你之後 我就發現 我可能愛上了你 雖然我們之間沒有過多的言語 甚至連面都沒見過幾面 你說話總是那麼的直率 那麼的不留臉面 你可能不知道你好幾次的話語都傷到了我自尊 但我不介意 當全然沒有發生過 我知道我們之間可能沒有希望 但我仍然堅持下去 不奢望你能做我女朋友 只希望你能懂我的心！', '120000000000027', '2b74c61d2ce0e87923220234f3619b7d');
INSERT INTO `love_letter` VALUES ('1200000000000270010', '匿名', '全世界', '我开始关心阳光的移动并像一株向光性极强的植物每天跟着太阳走。当我一个人坐着安静地傻笑我真想把其中的幸福说出来但我不能，我只能把一杯白开水，喝出甜味来。想你了                                                                                                            ', '120000000000027', '2b74c61d2ce0e87923220234f3619b7d');
