INSERT INTO `love_letter` VALUES ('1200000000000290008', '匿名', '彩良', '你说你是集美貌与才华于一身的女子，我就笑了。后来慢慢相处后发现，你真是如此。内在有一种气质，散发淡淡才华。人很好，脾气也很好，很懂事，很理性。过着慢生活，挺好的。相信了解你的人都会喜欢你，真的。', '120000000000029', '2b74c61d2ce0e87923220234f3619b7d');
