INSERT INTO `love_letter` VALUES ('1200000000000290011', '诗诗', '伍祖辉', '难以忘记初次见你，一双迷人的眼睛，使我沉迷。你的幽默，你的笑容，每一次和你见面，都是一场冒险，我害怕你看出我的心意。我只想偷偷告诉你，我爱你。', '120000000000029', '2b74c61d2ce0e87923220234f3619b7d');
