INSERT INTO `love_letter` VALUES ('1200000000000330022', '巴啦啦', '雯婷姐姐', '大一开始就注意你，我发现我配不上你，因为我矮，我没有表白，大二发现你有男朋友了，还很高，我觉得要祝福你，但心里是难受的，很多时候都想和你有个偶遇，能打个招呼，打个招呼我就觉得不错了，不奢求什么，祝你好好的。', '120000000000033', '2b74c61d2ce0e87923220234f3619b7d');
