INSERT INTO `love_letter` VALUES ('1200000000000350044', '匿名', '陈雅铃。', '从第一眼见到你我就被你吸引。你飘逸的长发，呆萌的模样深深的植在我心里。我通过各个渠道去找你。为了能经常偶遇你。还特地去查了你的课表。我相信陪伴是最长情的告白。也相信我的坚持能引起你的注意。我喜欢你。                                                                                                                 ', '120000000000035', '2b74c61d2ce0e87923220234f3619b7d');
