INSERT INTO `love_letter` VALUES ('1200000000000390026', 'Roger小南瓜', '杨晓君', '小西瓜，这个爱称来自你站在珠江边的一张穿着西瓜衣服的照片。小南瓜，就是纯粹你喜欢吃南瓜而已…一张照片，一片西瓜，几张车票，几餐饭堂还有各种各样我们才知道的回忆把我们带到了一起，我来到你的校园，陪你演过各种偶像剧才有的画面。话不多说，心照啦瓜儿！下次回去陪你走操场！荡秋千哈！赶在咱们都毕业前！爱老虎油！你不要欺负我到我需要擦铁打油哈！', '120000000000039', '2b74c61d2ce0e87923220234f3619b7d');
