INSERT INTO `love_letter` VALUES ('1200000000000410008', '大神', '小妖', '我们有好多事都无法知道，例如，生命的尽头，例如，你是否爱我，但是，我知道，我会记住你多久。。。', '120000000000041', '2b74c61d2ce0e87923220234f3619b7d');
