INSERT INTO `love_letter` VALUES ('1200000000000470035', 'D', 'HQ', '不知道怎么了，你不回我信息就焦虑不安，做什么事都没法专心，我也不知道从什么时候起，喜欢和你在一起，哪怕只是看着你，远远看着你', '120000000000047', '2b74c61d2ce0e87923220234f3619b7d');
