INSERT INTO `love_letter` VALUES ('1200000000000490022', '你的大狗狗', '小可爱', '有一天，我们一起起床，一起刷牙，一起拉着手出家门。一起遛狗，一起散步，一起为了争电视斗智斗勇。一起买菜，一起做饭，我知道，一定会有这么一天的。', '120000000000049', '2b74c61d2ce0e87923220234f3619b7d');
