INSERT INTO `love_letter` VALUES ('1200000000000520021', '敌方英雄', '大乔', '在上一周计协举行的王者荣耀比赛中，我遇见了你。当时你玩的英雄是大乔，虽然作为敌方队友，我对你的操作十分欣赏，你是我见过最不坑队友的大乔，无奈把我方坑的要死，虽然我曾十步杀一人，却还是败给了你一个眼神。大乔，我能与你再战一局吗？让我上墙！给我一个机会认识大乔！', '120000000000052', '2b74c61d2ce0e87923220234f3619b7d');
