INSERT INTO `love_letter` VALUES ('1200000000000550018', '匿名', '晚上12点游荡在金桂园到蓝楹楼的女生', '每天晚上刷牙时看到你在这里游荡，真替你捏了一把汗啊。晚12点后才出来，是因为这个钟点的网络总是突然变差，所以要出来接受信号吗？小心点哦', '120000000000055', '2b74c61d2ce0e87923220234f3619b7d');
