INSERT INTO `love_letter` VALUES ('1200000000000550024', '匿名', '某个人', '好感不代表喜欢，很久之后，我才知道那只是好感，不是喜欢。如果不是你忙，没时间找我，或许我永远都不会发现那只是好感，谢谢你，在我大学最开始的阶段，给了我一个好感。如果我们不偶尔聊天，或许我们会像失联了吧', '120000000000055', '2b74c61d2ce0e87923220234f3619b7d');
