INSERT INTO `love_letter` VALUES ('1200000000000550042', 'L珊', '何??', '异地恋最怕的就是失去信心和信任。我也相信我们最后会在一起，只要你不放弃。能遇上这么一个对你珍而重之而你也珍而重之的人，无怨无悔！彼此珍惜', '120000000000055', '2b74c61d2ce0e87923220234f3619b7d');
