INSERT INTO `love_letter` VALUES ('1200000000000600005', '匿名', '许枞', '还记得，第一学期上微观经济课的时候，你那甜美的微笑，让我一直记到现在，如春风般的迷人，我知道，你对我微笑，纯属礼貌。从头到脚，从内到外，都吸引着我，我知道你不喜欢我，我也知道，你现在并不适合拍拖，但我喜欢你就足够了，我会一直默默看着你的微笑，看着你幸福！用你想不到的方式，也在你看不到的地方！', '120000000000060', '2b74c61d2ce0e87923220234f3619b7d');
