INSERT INTO `love_letter` VALUES ('1200000000000700016', '淡淡的菊花茶香', '粉红色杯子的你', '淡淡的菊花茶香， 只有粉红色杯子见证那个过去！倾听那一优美文学的旋律， 蹭课spss的初次相遇， 图书馆二楼每天期待见到你的惊喜， 一个多月却没有言语， 悠悠的回旋歌曲， 偷看认真复习六级和雅思的你， 迷人不已！ 优雅而有气质， 14市营的你，', '120000000000070', '2b74c61d2ce0e87923220234f3619b7d');
