INSERT INTO `love_letter` VALUES ('1200000000000750029', '匿名', '75', '“你是春日里吹来花信的风，是夏夜里倒悬的星河，是秋风中渐行渐远的雁，是冬雪初霁时的暖阳闪烁着金边。星辰交替，四季更迭，而我的欢喜不变。”', '120000000000075', '2b74c61d2ce0e87923220234f3619b7d');
INSERT INTO `love_letter` VALUES ('1200000000000750030', '雷雷', '一营三连四排教官', '有些路，只能一个人走。绿萝拂过衣襟，青云打湿诺言。山和水可以两两相忘，日与月可以毫无瓜葛。那时，只一个人的浮世清欢。', '120000000000075', '2b74c61d2ce0e87923220234f3619b7d');
