INSERT INTO `love_letter` VALUES ('1200000000000750032', '匿名', '成长的你', '没有人会比我更爱你。你所未知的恐惧，我已替你经历。我爱你。我看着你，脱下球鞋，踩起高跟，摆起裙子。四年如一日，二十二载无倦意。我亲爱的自己。我看你，从懵懂无知，到玲珑心思。你所经历的伤痛，我已替你抚平。', '120000000000075', '2b74c61d2ce0e87923220234f3619b7d');
