INSERT INTO `love_letter` VALUES ('1200000000000760006', '崽崽', '笨蛋凯链', '好吧，恭贺你来到我的生活里，我的眼神里，我的心脏里，我的日记里，我的拥抱里。不在你身边，要照顾好自己，我在未来等你，也在此刻念你。不能相见的每一天，愿君三安，早安，午安，晚安。', '120000000000076', '2b74c61d2ce0e87923220234f3619b7d');
