INSERT INTO `love_letter` VALUES ('1200000000000760009', '有趣的灵魂万里挑一', '华软小姐姐', '我已经大一，也不小了，但是刚军训完，我现在好黑啊，行走在黑夜里只能看到一排雪白的牙齿而已，我好害怕啊，害怕小姐姐们不喜欢原本帅气的我了，但是我现在没了颜值，但是我还有才华啊，更何况我的颜值会回来的，所以说小姐姐们要尽快向我伸出触手啊，被人提前下手可就亏了啊…', '120000000000076', '2b74c61d2ce0e87923220234f3619b7d');
