INSERT INTO `love_letter` VALUES ('1200000000000770035', '匿名', '郭先生', '曾经把一个人当做自己的未来用尽眼泪和力气留下的只是些纠缠可后来被爱情甩了耳光才发觉想努力延长有你的时间', '120000000000077', '2b74c61d2ce0e87923220234f3619b7d');
INSERT INTO `love_letter` VALUES ('1200000000000770036', '匿名', '郭文晨', '可惜说出口的时候已经太晚了一年后的今天 我依然喜欢你我喜欢你 可惜因为没勇气一年前错过了', '120000000000077', '2b74c61d2ce0e87923220234f3619b7d');
INSERT INTO `love_letter` VALUES ('1200000000000770037', '你的猪猪', '阿政', '我本以为我是个很有原则的人，可是当遇见你，所有的原则不再是原则。', '120000000000077', '2b74c61d2ce0e87923220234f3619b7d');
