INSERT INTO `love_letter` VALUES ('1200000000000800043', '匿名', 'wsl', '每天看着你为部门和班上的事四处忙碌。每天没有充足的时间休息，看着有点心疼。你是一个外表装得很坚强，好像什么事都没关系，但是你确实是一个让人想保护的女孩。累了就停下来休息一下，不要勉强自己。要是缺个男朋友，可以考虑一下我啊', '120000000000080', '2b74c61d2ce0e87923220234f3619b7d');
