INSERT INTO `love_letter` VALUES ('1200000000000820035', 'PZH、', 'LSQ、', '我可以让你喜欢上我  ? 我可以做的事还很多，TO:我可以只对你一个人好，我可以忍受你所有的坏脾气，我可以过马路牵着你给你安全感，我可以变得有趣，我可以细心的记住你说的每一件事，我可以...我可以......', '120000000000082', '2b74c61d2ce0e87923220234f3619b7d');
