INSERT INTO `love_letter` VALUES ('1200000000000860018', '匿名', '红色上衣感冒的你', '26号周二的下午，乘坐广从5号回学校。你坐在我旁边穿红色上衣，衣袖上带一条黑色的带子，还有黄色的鞋子。从电话里听到了要拿什么证，从声音听得出你感冒了，其实也没什么事，就想问题感冒好了没，顺便问下你有没有男朋友。', '120000000000086', '2b74c61d2ce0e87923220234f3619b7d');
