INSERT INTO `love_letter` VALUES ('1200000000000900037', '匿名', '某某某', '不知道该不该发这样的一段话：当你喜欢我时，我还不喜欢你；但是当我慢慢喜欢上你时，以为自己能和你在一起时，你却喜欢上了别人！我不知道这是证明了什么，我只知道你好像还欠我一段恋爱，或许是不是可以还回来呢！或许回不去了吧！或许……算了吧！', '120000000000090', '2b74c61d2ce0e87923220234f3619b7d');
