INSERT INTO `love_letter` VALUES ('1200000000001010035', '匿名', '小丑', '每当我不记得你时，你却总会出现在我眼前，虽然每一次都是檫肩而过，但依然激起我们之间的回忆，每当听到或看到我们之间调腻过的话题，总会不自觉的想起你，我希望这是我最后一次还在想你，因为我不想等了，累了，俗话说得好：有一种遗忘叫取代。', '120000000000101', '2b74c61d2ce0e87923220234f3619b7d');
