INSERT INTO `love_letter` VALUES ('1200000000001120015', '15号蓝衣黄帽小姐姐', '15 小哥哥', '29号星期五下午饭堂卖茶叶时偶遇的小哥哥，现实中的你肯定是一个很nice的人，太喜欢你了。我买茶叶蛋1.5，然后阿姨打钱打错打成了15块，你在一旁说了个15，起初我还不知道是什么意思，最后你才解释说是阿姨打错钱打成了15块钱，我才恍然大悟。明人不说暗话，你人品这么好我好想认识你怎么办？', '120000000000112', '2b74c61d2ce0e87923220234f3619b7d');
