INSERT INTO `love_letter` VALUES ('1200000000001140004', '我把你备注越改越长了', '师兄', '上班竟然开始让你这头猪猪可以早睡早起，优秀的社会人你不再熬夜了，好开心啊~加油！距离我毕业还有两年！小屁孩我会一直陪着你的！我喜欢你我喜欢你我最喜欢你！好想抱抱下班回来的你！', '120000000000114', '2b74c61d2ce0e87923220234f3619b7d');
