INSERT INTO `love_letter` VALUES ('1200000000001170021', '那个和你对视的女孩', '地铁上偶遇的小哥哥', '希望还能再次相遇，我抱着千分之一的希望，期待每一天坐地铁回家都能遇到你真是确认过眼神，遇到对的人我每天都因为你让自己一天比一天更优秀希望你能遇见并认识最美好的我一上地铁我们两互相抬头互相对视，气氛就莫名其妙的羞涩尴尬起来了，期间互相偷偷望对方多次要怪就怪我不够勇敢，没有主动问你要微信', '120000000000117', '2b74c61d2ce0e87923220234f3619b7d');
