INSERT INTO `love_letter` VALUES ('1200000000001190011', '小胸弟', '大哥', '分手后，谢谢你还一直愿意陪我聊天，虽然有时后会跟你闹脾气，吵吵架，至少心里会舒服点，因为你还在我身边，没有离我而去。以前确实是有很多做的不好的地方，工作后也学会了反省自己，重新认识自己，了解自己。分手后第一个没有你在身边的七夕情人节，希望你也开心点，有空多去浪，别宅着。', '120000000000119', '2b74c61d2ce0e87923220234f3619b7d');
