INSERT INTO `love_letter` VALUES ('1200000000001190017', '呆呆', '二逼舍友们', '12月就要拍毕业照了，很快就要出去实习了，大学期间，遇到的人和事很多。有开心也有难过。有的舍友之间会有很多的矛盾，但是等到毕业时，你会发现，其实，你最不舍的还是她们。', '120000000000119', '2b74c61d2ce0e87923220234f3619b7d');
