INSERT INTO `love_letter` VALUES ('1200000000001230007', '偷偷注意你的男生', '在正门等车的长发黑裙女生', '11号星期二中午在正门等车，穿着黑色裙子，背着一个背包，手上挎着一个黑色手袋，披着有点卷的长发，后来上了5路公车的那个女生，之前在等车的时候就看到你很多次了，还跟你坐过同一辆大巴，我想认识你。', '120000000000123', '2b74c61d2ce0e87923220234f3619b7d');
