INSERT INTO `love_letter` VALUES ('1200000000001230012', '就不告诉你', '18财会小鲜肉们', '多买些藿香正气水放在宿舍，军训结束就回去喝，不要嫌弃药太苦，乖啦。多喝水！多喝水！多喝水！看你们不舒服，我也很难过师兄师姐忍痛把食堂留给你们，你们可不要不吃饭呀！认真军训的你们超级好看照顾好你们自己，', '120000000000123', '2b74c61d2ce0e87923220234f3619b7d');
