INSERT INTO `love_letter` VALUES ('1200000000001280005', '一个男孩', '路过的吃瓜群众', '我反复看了几遍你当时的留言说真的我当时也吓了一跳最后确认不是才松了一口气现在的我，也知道该怎么做了我也以为你是她谢谢你哈，那个路过的吃瓜群众谢谢啦。', '120000000000128', '2b74c61d2ce0e87923220234f3619b7d');
